  if( xDir  = =  −1  &&  x  = =  leftBound ) { 
    leftBound = Max( leftBound − 1, 0 ) 
    x = leftBound 
    ( xDir, yDir ) = ( 0, 2 * slice_group_change_direction_flag − 1 ) 
  } else if( xDir  = =  1  &&  x  = =  rightBound ) { 
    rightBound = Min( rightBound + 1, PicWidthInMbs − 1 ) 
    x = rightBound 
